import React from 'react';

interface NewsItem {
  title: string;
  date: string;
  desc: string;
}

const news: NewsItem[] = [
  {
    title: 'Stem cells: What they are and what they do',
    date: 'Jan 08, 2025',
    desc: 'Learn about the basics of stem cells and their potential in medical research.'
  },
  {
    title: 'Stem cells: What they are and what they do',
    date: 'Jan 08, 2025',
    desc: 'Learn about the basics of stem cells and their potential in medical research.'
  },
  {
    title: 'Stem cells: What they are and what they do',
    date: 'Jan 08, 2025',
    desc: 'Learn about the basics of stem cells and their potential in medical research.'
  }
];

const NewsSection: React.FC = () => (
  <section className="py-12 bg-gray-50">
    <h2 className="text-xl font-bold text-center mb-8">Our Latest <span className="text-blue-700">News</span></h2>
    <div className="flex justify-center gap-8">
      {news.map((item, idx) => (
        <div key={idx} className="bg-white shadow rounded p-4 w-80">
          <div className="h-32 bg-blue-100 mb-4 rounded"></div>
          <h3 className="font-bold mb-2">{item.title}</h3>
          <p className="text-sm mb-2">{item.desc}</p>
          <div className="text-xs text-gray-500">{item.date}</div>
        </div>
      ))}
    </div>
    <div className="flex justify-center mt-6">
      <button className="bg-orange-500 text-white px-6 py-2 rounded">View All</button>
    </div>
  </section>
);

export default NewsSection;
