import React from 'react';

const HeroSection: React.FC = () => (
  <section className="text-white py-20 px-8" style={{ backgroundColor: 'var(--primary-blue)', backgroundImage: 'url(/hero-bg.jpg)', backgroundSize: 'cover' }}>
    <div className="max-w-2xl">
      <h1 className="text-3xl font-bold mb-2">Together, <br />We Can Advance <br />Life-Saving Research!</h1>
      <p className="mb-6">Join us in supporting cutting-edge research that brings us closer to a cure.</p>
      <button className="px-6 py-2 rounded font-medium transition-colors" style={{ backgroundColor: 'var(--accent-orange)', color: 'var(--white)' }} onMouseOver={(e) => e.currentTarget.style.backgroundColor = '#E67A5A'} onMouseOut={(e) => e.currentTarget.style.backgroundColor = 'var(--accent-orange)'}>
        Explore Our Research
      </button>
    </div>
  </section>
);

export default HeroSection;
