import React from 'react';

interface Card {
  title: string;
  text: string;
}

const cards: Card[] = [
  {
    title: 'Our Vision',
    text: 'To advance stem cell breakthroughs globally, through global partnerships, securing long-term investment, and inspiring new research, making a difference for generations.'
  },
  {
    title: 'Our Mission',
    text: 'To fund and foster stem cell research and deliver promising treatments and cures, using scientific teams, community, and donors, accelerating health outcomes and hope for generations to come.'
  },
  {
    title: 'Our Values',
    text: 'We value innovation, collaboration, and inclusivity in our mission to save lives. Guided by science, integrity, and care, we strive to make a difference responsibly and effectively.'
  }
];

const VisionMissionValues: React.FC = () => (
  <section className="py-12 flex justify-center" style={{ backgroundColor: 'var(--light-gray)' }}>
    <div className="flex gap-8">
      {cards.map((card, idx) => (
        <div key={idx} className="shadow rounded p-6 w-80" style={{ backgroundColor: 'var(--white)' }}>
          <h3 className="font-bold text-lg mb-2" style={{ color: 'var(--primary-blue)' }}>{card.title}</h3>
          <p style={{ color: 'var(--text-dark)' }}>{card.text}</p>
        </div>
      ))}
    </div>
  </section>
);

export default VisionMissionValues;
