import React from 'react';

interface Stat {
  label: string;
  value: string;
  desc: string;
}

const stats: Stat[] = [
  { label: 'Research Projects', value: '120+', desc: 'Supported across institutions worldwide.' },
  { label: 'Clinical Trials', value: '30+', desc: 'Accelerating therapies from the lab to the clinic.' },
  { label: 'Researchers Funded', value: '50+', desc: 'Leading scientists working on stem cell research.' },
  { label: 'Raised', value: '$15M+', desc: 'Funds invested directly into research.' },
  { label: 'Donors', value: '5,000+', desc: 'Individuals and organizations funding a cure.' }
];

const ImpactStats: React.FC = () => (
  <section className="py-12 flex flex-col items-center bg-white">
    <h2 className="text-xl font-bold mb-8">Your Contribution <span className="text-blue-700">Makes All The Difference</span></h2>
    <div className="flex flex-wrap justify-center gap-8">
      {stats.map((stat, idx) => (
        <div key={idx} className="bg-blue-50 rounded-lg shadow p-6 w-48 flex flex-col items-center">
          <div className="text-2xl font-bold text-blue-900 mb-2">{stat.value}</div>
          <div className="font-semibold mb-1">{stat.label}</div>
          <div className="text-sm text-gray-600 text-center">{stat.desc}</div>
        </div>
      ))}
    </div>
  </section>
);

export default ImpactStats;
